import React, {useState, Fragment} from "react";
import classes from './Oglasi.css';
import Main from "../../components/Main/Main";
import Oglas from "./Oglas/Oglas";
import ToolbarLayout from "../../components/ToolbarLayout/ToolbarLayout";
import AuthApi from "../../auth-api";
import Cookies from 'js-cookie';
import gql from 'graphql-tag';
import {useQuery} from '@apollo/react-hooks';


const Oglasi = (props) => {

    const [imamStan, setImamStatn] = useState(true);
    const [oglasi, setOglasi] = useState([]);
    const Auth = React.useContext(AuthApi);

    const POSTS_QUERY = gql`
    query {
        posts {
        post_id
        description
        address
        rent
        num_of_roommates
        author {
        user_id
        username
        name
        surname
        city
        address
        email
        bio
        gender
        date_of_birth
        pr_picture_url
        pref_roommate_num
        }
    }
}`;
    const user = Cookies.get("user");
    const token = user.toString().split('"')[3];

    const { loading, error, data } = useQuery(POSTS_QUERY, {
        context: {
            headers: {
                "Authorization": token
            }
        }
    })

    if (loading) return <p>Učitavanje...</p>
    if (error) return <p>{`Greška: ${error.message}`}</p>

    /* Uraditi renderovanje data varijable u Oglase */
    /* Nakon ovoga uraditi dodavanje oglasa */
    console.log(data.posts);

    return (
        <ToolbarLayout>
            <Main>
                <div className={classes.Oglasi}>
                    <div className={classes.Buttons}>
                        <button>IMAM STAN</button>
                        <button>NEMAM STAN</button>
                    </div>
                    <h1>Oglasi</h1>
                    <div className={classes.ListaMisljenja}>
                        {
                            data.posts.map(post => {
                                console.log(post.author.name)
                                return <Oglas 
                                    text={post.description}
                                    brojCimera={post.num_of_roommates}
                                    cijena={post.rent}
                                    ime={post.author.name}
                                    prezime={post.author.surname}
                                    email={post.author.email}
                                    />
                            })
                        }
                    </div>
                </div>
            </Main>
        </ToolbarLayout>
    )
}

export default Oglasi;
